load_more_button_css:
@media_boxes .media-boxes-load-more-button{

    font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif;
    cursor: pointer;
    width: 150px;
    text-align: center;
    color: white;
    background-color: #333333;
    font-size: 14px !important;
    height: 15px;
    padding: 10px 15px 10px 15px;
    margin: 0px auto;
    line-height: 15px;

    -webkit-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
       -moz-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
         -o-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
        -ms-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
            box-shadow: 0 1px 1px rgba(34,25,25,0.2);

    -webkit-box-sizing: content-box !important;
       -moz-box-sizing: content-box !important;
            box-sizing: content-box !important;

}

@media_boxes .media-boxes-loading{

}

@media_boxes .media-boxes-no-more-entries{

    filter: alpha(opacity=20);
    opacity: 0.2;

}

css:
/* Change filter style */
@media_boxes .media-boxes-filters-container {
        margin-bottom: 20px;
        text-align: right;
}

@media_boxes .media-boxes-filter>li,
@media_boxes .media-boxes-filter>li:last-child{
	margin: 0 0 0 18px !important;
}
@media_boxes .media-boxes-filter li>a{
	background-color: transparent;
	padding: 0;
	height: auto;
	line-height: normal;
	border: none;
	color: #999
} 	
@media_boxes .media-boxes-filter>li a.selected{
	background-color: transparent !important;
	box-shadow: none !important;
	color: #D1474C !important;
}
@media_boxes .media-boxes-filter>li a:hover {
    text-decoration: none;
    background: transparent !important;
    color: #333333;
}

/* hide no more entries button */
@media_boxes .media-boxes-no-more-entries{
	display: none;
}

/* hover effect on the image */
@media_boxes .media-box-image img{
    -webkit-transition: transform 0.6s ease-in-out;
       -moz-transition: transform 0.6s ease-in-out;
         -o-transition: transform 0.6s ease-in-out;
        -ms-transition: transform 0.6s ease-in-out;
            transition: transform 0.6s ease-in-out;

    -webkit-transform-origin: bottom left;
       -moz-transform-origin: bottom left;
         -o-transform-origin: bottom left;
        -ms-transform-origin: bottom left;
            transform-origin: bottom left;
}
@media_boxes .media-box-image:hover img{
    -webkit-transform: scale(1.2) translate(-20px);
       -moz-transform: scale(1.2) translate(-20px);
         -o-transform: scale(1.2) translate(-20px);
        -ms-transform: scale(1.2) translate(-20px);
            transform: scale(1.2) translate(-20px);
}
