load_more_button_css:
@media_boxes .media-boxes-load-more-button{

    font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif;
    cursor: pointer;
    width: 150px;
    text-align: center;
    color: white;
    background-color: #333333;
    font-size: 14px !important;
    height: 15px;
    padding: 10px 15px 10px 15px;
    margin: 0px auto;
    line-height: 15px;

    -webkit-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
       -moz-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
         -o-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
        -ms-box-shadow: 0 1px 1px rgba(34,25,25,0.2);
            box-shadow: 0 1px 1px rgba(34,25,25,0.2);

    -webkit-box-sizing: content-box !important;
       -moz-box-sizing: content-box !important;
            box-sizing: content-box !important;

}

@media_boxes .media-boxes-loading{

}

@media_boxes .media-boxes-no-more-entries{

    filter: alpha(opacity=20);
    opacity: 0.2;

}

css:
/* Change the color red to something blue */
@media_boxes .media-boxes-filter>li a.selected {
	background: #1cbac8 !important;
}
@media_boxes .media-box-quote{
	background: #1cbac8;
}
@media_boxes .thumbnail-overlay-button{
	background: rgba(28, 186, 200, 1);
	color: #fff;
}
@media_boxes .thumbnail-overlay-button:hover{
	background: rgba(28, 186, 200, 1);
}

/* Center the filters */
@media_boxes .media-boxes-filters-container{
	text-align: center;
        margin-bottom: 30px;
}

/* change style of filter menu */
@media_boxes .media-boxes-filter>li {
	margin: 0 !important;
}
@media_boxes .media-boxes-filter>li:last-child a{
	border-right: 1px solid #EBEBEB;
	border-radius: 0 4px 4px 0;
}
@media_boxes .media-boxes-filter>li:first-child a{
	border-radius: 4px 0 0 4px;
}
@media_boxes .media-boxes-filter>li a {
	border-bottom: 1px solid #EBEBEB;
	  border-left: 1px solid #EBEBEB;
	   border-top: 1px solid #EBEBEB;
	 border-right: none;

	-webkit-box-shadow: none;
	   -moz-box-shadow: none;
  	     -o-box-shadow: none;
        -ms-box-shadow: none;
  		    box-shadow: none;
}
@media_boxes .media-boxes-filter>li a.selected {
    -webkit-box-shadow: none !important;
       -moz-box-shadow: none !important;
        -ms-box-shadow: none !important;
            box-shadow: none !important;
}
@media_boxes .media-boxes-filter>li a:hover{
    background: #fff;
    color: #676767;
}
